            <p class="userAddress">联系地址：{{ userAddress }}</p>
        </div>
        <div class="brief" v-show="activeView === 'selection'">
        <h1 class="title">纪念品兑换</h1>
            <p class="totalPoints">总积分：{{ userPoints }}</p>
        </div>

        <div v-for="gift in gifts" 
            :key="gift.id" 
            :id="'giftCard'+gift.id"
            class="itemCard" 
            v-show="activeView === 'selection' || selectedGifts.get(gift.id) > 0">

            <div class="imgContainer">
                <img :src=gift.image
                    :alt="gift.name" 
                    @click="showDetail(gift.styles)"
                    @click.stop
                    class="itemImg">
            </div>

            <div class="itemInfo">
                <h2 class="itemName">{{ gift.name }}</h2>
                <p class="discription">款式: {{ gift.description }}</p>
            </div>

            <div class="priceContainer">
                <h3 class="itemPrice">{{ gift.points }} 积分</h3>
            </div>
            <button 
                class="addItem" :class="{ active: !selectedGifts.has(gift.id) || selectedGifts.get(gift.id) == 0 }"
                @click="addToCart(gift)"
                v-show="activeView === 'selection'">
                <svg class="add" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512">
                <path d="M256 80c0-17.7-14.3-32-32-32s-32 14.3-32 32l0 144L48 224c-17.7 0-32 14.3-32 32s14.3 32 32 32l144 0 0 144c0 17.7 14.3 32 32 32s32-14.3 32-32l0-144 144 0c17.7 0 32-14.3 32-32s-14.3-32-32-32l-144 0 0-144z"/>
                </svg>
            </button>
            <div class="quantityCount" 
                    :class="{ active: selectedGifts.has(gift.id) && selectedGifts.get(gift.id) > 0}" 
                    v-show="activeView === 'selection'">
                <button @click="removeFromCart(gift)">
                    <svg class="minus" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512">
                    <path d="M432 256c0 17.7-14.3 32-32 32L48 288c-17.7 0-32-14.3-32-32s14.3-32 32-32l352 0c17.7 0 32 14.3 32 32z"/>
                    </svg>
                </button>
                <p class="count">{{ selectedGifts.get(gift.id) }}</p>
                <button @click="addToCart(gift)">
                    <svg class="plus" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512">
                    <path d="M256 80c0-17.7-14.3-32-32-32s-32 14.3-32 32l0 144L48 224c-17.7 0-32 14.3-32 32s14.3 32 32 32l144 0 0 144c0 17.7 14.3 32 32 32s32-14.3 32-32l0-144 144 0c17.7 0 32-14.3 32-32s-14.3-32-32-32l-144 0 0-144z"/>
                    </svg>
                </button>
            </div>
            <div class="summary"
                v-show="activeView === 'submit'">
                <p class="quantity">×{{ selectedGifts.get(gift.id) }}</p>
            </div>
        </div>
        <div class="total" v-show="activeView === 'submit'">
            <h2 class="totalValue">合计：<p>{{ pointsNeed }}</p>积分</h2>
        </div>
        <div class="butContainer">
            <button class="cancel" 
                @click="cancelOrder"
                v-show="activeView === 'submit'">
                取消订单
            </button>
            <button class="confirm" @click="checkOrder">
                <p>点击兑换</p>
                <p>需要积分：{{ pointsNeed }}分</p>
            </button>
        </div>
    </section>

    <section class="checkDetail" 
            :class="{ active: isDetailActive }" 
            >
            <ul class="itemList">
                <li 
                v-for="(feature, index) in currentStyles" 
                :key="index" 
                class="detailPic"
                >
                    <img :src="feature.image" :alt="feature.name">
                    <span>{{ feature.name }}</span>
                </li>
            </ul>
    </section>

    <Footer v-show="activeView === 'selection'"/>
</div>
</template>

<script setup>
import Footer from '@/components/Footer.vue';
import { onMounted, ref, onUnmounted } from 'vue';
import { gifts } from "../assets/dataset/gifts_list.js";

// this is for later use if we want to use props
// defineProps({
//     userName: {
//         type: String,
//         required: true,
//         default: '张某'
//     },
//     userContact: {
//         type: String,
//         required: true,
//         default: '1234567890'
//     },
//     userAddress: {
//         type: String,
//         required: true,
//         default: '北京市海淀区'
//     },
//     userPoints: {
//         type: Number,
//         required: true,
//         default: 1000
//     }
// })

const userPoints = ref(1000);
const userName = ref('张某');
const userContact = ref('1234567890');
const userAddress = ref('北京市海淀区');

const pointsNeed = ref(0);
const selectedGifts = ref(new Map())
const currentStyles = ref([]);
const isDetailActive = ref(false)
const activeView = ref('selection')

onMounted(() => {
    window.scrollTo(0, 0)
})

onUnmounted(() => {
    document.removeEventListener('click', handleClickOutside)
})

function addToCart(gift) {
    const currentCount = selectedGifts.value.get(gift.id) || 0
    selectedGifts.value.set(gift.id, currentCount + 1)
    pointsNeed.value += gift.points
}

function removeFromCart(gift) {
    const currentCount = selectedGifts.value.get(gift.id) || 0
    if (currentCount > 0) {
        selectedGifts.value.set(gift.id, currentCount - 1)
        pointsNeed.value -= gift.points
    }
}

function closeDetail() {
    isDetailActive.value = false
    currentStyles.value = []
    document.removeEventListener('click', handleClickOutside)
}

function handleClickOutside(event) {
    const detailPart = document.querySelector('.itemList')
    if (!detailPart.contains(event.target)) {
        closeDetail()
    }
}

function showDetail(styles) {
    currentStyles.value = styles
    isDetailActive.value = true
    const detailPage = document.querySelector('.checkDetail')
    document.addEventListener('click', handleClickOutside)
}

function checkOrder() {
    if (activeView.value === 'selection') {
        if (pointsNeed.value > userPoints.value) {
            alert('积分不足！')
        } else if (pointsNeed.value > 0) {
            activeView.value = "submit"
        } else {
            alert('请先选择纪念品再进行兑换！')
        }
    } else {
        alert('兑换成功！')
        userPoints.value -= pointsNeed.value
        pointsNeed.value = 0
        selectedGifts.value.clear()
        activeView.value = "selection"
    }
}

function cancelOrder() {
    activeView.value = "selection"
    selectedGifts.value.clear()
    pointsNeed.value = 0
    alert('订单已取消！')
}
</script>

